import React from "react"
import { useNavigate } from "react-router-dom"
import { useSelector } from 'react-redux';
import { products } from '../../constants/products';
import { routes } from "../../App";
import Card from '../../components/Card';
import background from '../../assets/login.jpg';
import './ProductsPage.scss';

function ProductsPage() {
  const navigate = useNavigate()

  const { isAuth } = useSelector(state => state.user)
  const { basketList, sum } = useSelector(state => state.basket)

  if (isAuth === false) {
    return (
      <div className='login' style={{ backgroundImage: `url(${background})` }}>
            <form className='login__form' onSubmit={handleSubmit}>
                <div className='login__form-link'>
                    <Link to={'/'} className='login__form-link-txt'>Зарегистрироваться</Link>
                </div>
                <h2 className='login__form-title'>вход</h2>

                <input className='login__form-input' type="text" value={login}
                    onChange={(e) => setLogin(e.target.value)}
                    placeholder="Логин" required={true} />

                <input type="password" className='login__form-input' value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Пароль" required={true} />

                <div className="login__checkbox">
                    <input className="login__checkbox-chbox" type="checkbox" id="checkbox" required/>
                    <label className="login__checkbox-label" for="checkbox"
                    >Я согласен получать обновления на почту</label>
                </div>

                <button className='login__button' type="submit">Войти</button>


            </form>
        </div>
    )
  }

  return (

    <main className="menu">
      <div className="container">
        <header className="menu__header">
          <div >
            <h1 className="menu__header-title">наша продукция</h1>
          </div>
          <div className="goods__header-order">
            <p className="goods__header-qty">
              <span>{basketList.length}</span>&nbsp;товара
              на сумму <span>{sum}</span>&nbsp;&#8381;
            </p>
            <button className='goods__header-icon'
              onClick={() => navigate(routes.cart)}>
              <img src="/images/1.svg" alt="Корзина"></img>
            </button>
            <button className='goods__header-button' type="submit" onClick={() => navigate(routes.login)}>Выйти</button>
          </div>
        </header>


        <ul className="menu__cards">
          {products.map(card => {
            return (
              <li key={card.id}>
                <Card
                  card={card}
                />
              </li>
            )
          })}
        </ul>
      </div>
    </main>
  );
}

export default ProductsPage;
