    if (login && password) {
      setAllow(true)
    }
  }, [])

  if (allow === false) {
    return (
      <div>
        <p>Menu is not allowed. Login first.</p>
        <button onClick={() => navigate('/login')}>Вход</button>
      </div>
    )
  }

  return (

    <main className="main">
      <div className="container">
        <header className="header">
          <div >
            <h1 className="products__title">наша продукция</h1>
          </div>

          <Link to={'/cart'} className="products__order">
            <p>
              {`${basketList.length} товара на сумму ${sum}`}
            </p>
            {<img className="products__img" src="/images/1.svg" />}
          </Link>
        </header>


        <ul className="menu">
          {products.map(card => {
            return (
              <li key={card.id}>
                <Card
                  card={card}
                />

              </li>
            )
          })}
        </ul>
      </div>
    </main>
  );
}

export default ProductsPage;
