import './BasketPage.css'
import React, { useState, useEffect } from "react"
import { useSelector } from 'react-redux'
import { useContext } from 'react'
import { AppContext } from '../../components/context/AppContext'
import { useDispatch } from 'react-redux'
import { removeProduct } from '../../redux/basket/basketSlice'

function BasketPage() {

    // const {list, setList} = useContext(AppContext)
    const {basketList, sum} = useSelector(state => state.basket)
   const dispatch = useDispatch()

    useEffect(() => {
        // const savedList = localStorage.getItem('savedList')
        // console.log('savedList', savedList)
        // if (savedList) {
        //     setList(JSON.parse(savedList))
        // }
    }, [])


    const handleClick = (card) => {
        dispatch(removeProduct(card))
    }
    return (
        <div className='basket__container'>
            <header>
                <h1 className='basket__title'>Корзина с выбранными товарами</h1>
            </header>
            <main className='basket__main'>
                { basketList.length > 0 ?
                <ul className='basket__list'>
                    {
                        basketList.map((x, index) =>
                            <li key={index} className='basket__item'>
                                <div className='basket__description'>
                                     <img src={x.url} className='basket__img' />
                                    <h3 className='basket__name'>{x.title}</h3>
                                </div>

                                <div className='basket__action'>
                                    <span className='basket__price'>{x.price}&nbsp;&#8381;</span>
                                    <button className='basket__btn' onClick={() =>
                                        handleClick(x)}>x</button>
                                </div> 

                            </li>
                        )
                    }



                </ul> :
                <h5>Корзина пуста</h5>
            }
            </main>
            <footer className='basket__footer'>
                <h2 className='basket__footer-order'>Заказ на сумму: <span className='basket__footer-total'>{sum}&nbsp;&#8381;</span></h2>
                <button className='basket__footer-btn'>Оформить заказ</button>
            </footer>
        </div>
    )
}

export default BasketPage;
