import React from 'react';
import { useDispatch } from 'react-redux';
import { addProduct } from '../redux/basket/basketSlice';
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from 'uuid';
import './Card.scss';
import { useMemo } from 'react';

const Card = ({ card }) => {
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const { url, description, title, price, weight, quantity, id } = card

    const handleBtn = (e) => {
        e.stopPropagation()
        let item = {...card, idx: uuidv4()}
        dispatch(addProduct(item))
    }
    //выше разворачиваем предыдущее значение карточки и добавляем новый ключ


    const handleCard = () => navigate(`/goods/${id}`)

    const aString = useMemo(() => weight ? `${weight} гр.` : `${quantity} шт.` [weight, quantity])

    console.log('aString', aString)

    return (
        <div className="card" onClick={handleCard}>
            <div className="card__top">
                <img className="card__top-img" src={url} alt="" />
                <h2 className="card__top-title">{title}</h2>
                <p className="card__top-description">
                    {description}
                </p>
            </div>

            <div className="card__footer">
                <div className="card__footer-info">
                    <span className="card__footer-price">{price}&nbsp;&#8381;</span>
                    <span className="card__footer-weight">/{aString}</span>
                </div>
                <button onClick={handleBtn} className='card__footer-btn'>&#43;</button>
            </div>

        </div>
    );
}

export default Card;
