import React, { useState } from "react";
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { setIsAuth } from '../../redux/user/userSlice';
import { setCredentials } from '../../redux/user/userSlice';
import { routes } from '../../App';
import background from '../../assets/login.jpg';
import './LoginPage.scss';

import { loginUser } from "../../constants/usersDB";

const users = [
    {
        login: 'user1',
        password: '123'
    },
    {
        login: 'user2',
        password: '321'
    }
]
//на странице бэкэнда. Пока оставляю здесь, чтобы работала кнопка

const LoginPage = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const { isAuth } = useSelector(state => state.user)

    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')

    //ниже-новая версия кода тест
    useEffect(() => {
        const lsLogin = localStorage.getItem('login')
        const lsPassword = localStorage.getItem('password')
        if (lsLogin && lsPassword) {
            const currentUser = loginUser
            dispatch(setIsAuth(true))
        }
    }, [])

    const handleSubmit = (e) => {
        e.preventDefault()
        const user = users.find(user => user.login === login && user.password === password)
        if (!user) {
            alert('Пользователь не найден. Зарегистрируйтесь.')

            dispatch(setIsAuth(false))
            return
        }

        if (user) {
            dispatch(setIsAuth(true))
            dispatch(setCredentials(user))
            localStorage.setItem('login', login)
            localStorage.setItem('password', password)
            navigate(routes.menu)
            return
        }
    }

    if (isAuth) {
        navigate(routes.menu)
        return null
    }

    return (
        <div className='login' style={{ backgroundImage: `url(${background})` }}>
            <form className='login__form' onSubmit={handleSubmit}>
                <div className='login__form-link'>
                    <Link to={'/'} className='login__form-link-txt'>Зарегистрироваться</Link>
                </div>
                <h2 className='login__form-title'>вход</h2>

                <input className='login__form-input' type="text" value={login}
                    onChange={(e) => setLogin(e.target.value)}
                    placeholder="Логин" required={true} />

                <input type="password" className='login__form-input' value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Пароль" required={true} />

                <div className="login__checkbox">
                    <input className="login__checkbox-chbox" type="checkbox" id="checkbox" required />
                    <label className="login__checkbox-label" for="checkbox"
                    >Я согласен получать обновления на почту</label>
                </div>

                <button className='login__button' type="submit">Войти</button>


            </form>
        </div>
    )
}

export default LoginPage;
