import React from 'react';
import './Card.css';
import { products } from '../products';
import { useDispatch } from 'react-redux';
import { addProduct } from '../redux/basket/basketSlice';
import { useContext } from 'react';
import { AppContext } from './context/AppContext';


const Card = ({ card }) => {
    const {setList} = useContext(AppContext)

    const { url, description, title, price, weigth } = card

    const dispatch = useDispatch()
    // let item = {
    //     title: title,
    //     url: url,
    //     price: price
    // }

    const handleClick = () => {
        dispatch(addProduct(card))


        setList(prevState => {
            const res = [...prevState, card]
            return res
        })
    }

    return (
        <div className="card">
            <div className="card__top">
                <img className="card__preview" src={url} alt="" />
                <h2 className="card__title">{title}</h2>
                <p className="card__description">
                    {description}
                </p>
            </div>

            <div className="card__footer">
                <div className="card__price">
                    {/* {price} */}
                    {`${price}/ ${sum}`}
                </div>
                <button onClick={handleClick} className='card__price-btn'>
                    +
                </button>
            </div>

        </div>
    );
}

export default Card;
