import React, { useEffect } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useSelector, useDispatch } from 'react-redux';
import Card from '../../components/Card';
import { products } from '../../constants/products'
import './ProductsPage.css'

import { setIsAuth } from '../../redux/user/userSlice';


function ProductsPage() {
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const {isAuth, user} = useSelector(state => state.user)
  const { basketList, sum } = useSelector(state => state.basket)


  useEffect(() => {
    const login = localStorage.getItem('login')
    const password = localStorage.getItem('password')
    if (login && password) {
      dispatch(setIsAuth(true))
    }
  }, [])

  if (isAuth === false) {
    return (
      <div>
        <p>Menu is not allowed. Login first.</p>
        <button onClick={() => navigate('/login')}>Вход</button>
      </div>
    )
  }

  return (

    <main className="main">
      <div className="container">
        <header className="header">
          <div >
            <h1 className="products__title">наша продукция</h1>
          </div>

          <Link to={'/cart'} className="products__order">
            <p>
              {`${basketList.length} товара на сумму ${sum}`}&nbsp;&#8381;
            </p>
            {<img className="products__img" src="/images/1.svg" />}
          </Link>
        </header>


        <ul className="menu">
          {products.map(card => {
            return (
              <li key={card.id}>
                <Card
                  card={card}
                />

              </li>
            )
          })}
        </ul>
      </div>
    </main>
  );
}

export default ProductsPage;
