import React, { useState } from "react";
import { useNavigate, Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { setIsAuth } from '../../redux/user/userSlice';
import { routes } from '../../App';
import { registerUser, getAllUsers, loginUser } from "../../constants/usersDB";
import '../LoginPage/LoginPage.module.scss';


const RegisterPage = () => {
    const navigate = useNavigate()
    const dispatch = useDispatch()

    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')

    const handleSubmit = (e) => {
        e.preventDefault()

        const user = loginUser({
            login,
            password
        })

        if (!user) {
            registerUser({
                login,
                password
            })
            dispatch(setIsAuth(true))
            localStorage.setItem('login', login)
            localStorage.setItem('password', password)
            navigate(routes.menu)
        }

        if (user) {
            alert('Такой пользователь уже зарегистрирован. Авторизуйтесь')
            navigate(routes.login)
        }
    }

    return (
        <div className='login'>

            <button onClick={() => console.log(getAllUsers())}>getallusers</button>

            <form className='login__form' onSubmit={handleSubmit}>
                <div className='login__form-link'>
                    <Link to={'/login'} className='login__form-link-txt'>Авторизоваться</Link>
                </div>
                <h2 className='login__form-title'>Регистрация</h2>


                <input className='login__form-input' type="text" value={login}
                    onChange={(e) => setLogin(e.target.value)}
                    placeholder="Логин" required={true} />

                <input type="password" className='login__form-input' value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Пароль" required={true} />

                <div className="login__checkbox">
                    <input className="login__checkbox-chbox" type="checkbox" id="checkbox" required />
                    <label className="login__checkbox-label" for="checkbox"
                    >Я согласен получать обновления на почту</label>
                </div>

                <button className='login__button' type="submit">Зарегистрироваться</button>
            </form>
        </div>
    )
}
export default RegisterPage;
