import React from "react"
import { Link, useNavigate } from "react-router-dom"
import { useSelector } from 'react-redux';
import Card from '../../components/Card';
import { products } from '../../constants/products';
import { routes } from "../../App";
import './ProductsPage.css';

function ProductsPage() {
  const navigate = useNavigate()

  const {isAuth} = useSelector(state => state.user)
  const { basketList, sum } = useSelector(state => state.basket)

  if (isAuth === false) {
    return (
      <div>
        <p>Menu is not allowed. Login first.</p>
        <button onClick={() => navigate(routes.login)}>Вход</button>
      </div>
    )
  }

  return (

    <main className="main">
      <div className="container">
        <header className="header">
          <div >
            <h1 className="products__title">наша продукция</h1>
          </div>

          {/* <Link to={'/cart'} className="products__order">
            <p>
              {`${basketList.length} товара на сумму ${sum}`}&nbsp;&#8381;
            </p>
            {<img className="products__img" src="/images/1.svg" />}
          </Link> */}

<div className="goods__header-order">
                        <p className="goods__header-qty">
                            <span>{basketList.length}</span>&nbsp;товара
                            на сумму <span>{sum}</span>&nbsp;&#8381;
                        </p>
                        <button className='goods__header-icon'
                        onClick={() => navigate(routes.cart)}>
                        <img src="/images/1.svg" alt="Корзина"></img>
                    </button>
                        <button className='goods__header-button' type="submit" onClick={() => navigate(routes.login)}>Выйти</button>
                    </div>
        </header>


        <ul className="menu">
          {products.map(card => {
            return (
              <li key={card.id}>
                <Card
                  card={card}
                />
              </li>
            )
          })}
        </ul>
      </div>
    </main>
  );
}

export default ProductsPage;
