import React from 'react';
import './Card.css';
import { products } from '../products';

import { useDispatch } from 'react-redux';
import { addProduct } from '../redux/basket/basketSlice';
import { useContext } from 'react';
import { AppContext } from './context/AppContext';
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from 'uuid';

const Card = ({ card }) => {

    const { url, description, title, price, weight, quantity, id } = card

    const dispatch = useDispatch()
    const navigate = useNavigate()

    // const handleClick = () => {
    //     dispatch(addProduct(card))

    //     setList(prevState => {
    //         const res = [...prevState, card]
    //         return res
    //     })
    // }

    //вариант из вчерашней лекции - такое же в моем коде на стр пoodspage
    // const item = {...card, id: uuidv4()}
    // const handleAddClick = () => {
    //     dispatch(addProduct(item))
    // }

    const handleClick = (e) => {
        e.stopPropagation()
        let item = {
          id: id,
          idx: uuidv4(),
          title: title,
          url: url,
          price: price,

        }
        console.log('item', item)
        dispatch(addProduct(item))
      }



    const handleCard = () => {
        const item = {
            id: id,
            idx: uuidv4(),
            url: url,
            price: price,
            description: description,
            weight: weight,
            quantity: quantity
        }

        navigate('/goods' + '/' + id)
    }




    return (
        <div className="card" >
            <div className="card__top">
                <img className="card__preview" src={url} alt="" onClick={handleCard}/>
                <h2 className="card__title">{title}</h2>
                <p className="card__description">
                    {description}
                </p>
            </div>

            <div className="card__footer">
                <div className="card__price">
                    {`${price}/ ${weight}`}
                </div>
                <button onClick={handleClick} className='card__price-btn'>
                    +
                </button>
            </div>

        </div>
    );
}

export default Card;
