import { createSlice } from "@reduxjs/toolkit";

const basketSlice = createSlice({
    name: 'basket',
    initialState: {
        basketList: [],
        sum: 0
    },
    reducers: {
        addProduct(state, action) {
            // console.log('addProduct');
