import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { addProduct } from '../../redux/basket/basketSlice';
import { useDispatch } from 'react-redux';
import { getGood } from "../../constants/products";
import { v4 as uuidv4 } from 'uuid';
import Header from '../../components/Header/Header';
import ArrowButton from '../../components/ArrowButton/ArrowButton';
import './GoodsPage.scss'

const GoodsPage = () => {
    const dispatch = useDispatch()

    const { id } = useParams()
    const [good, setGood] = useState()
    const [card, setCard] = useState()

    useEffect(() => {
        if (id) {
            const goodById = getGood(id)
            setGood(goodById)
        }
    }, [id])

    if (!good) {
        return (
            <div>Loading...</div>
        )
    }

    const handleClick = () => {
        const goodWithUniqId = { ...good, idx: uuidv4() }
        dispatch(addProduct(goodWithUniqId))
    }
    // let item = {...card, idx: uuidv4()}
    // dispatch(addProduct(item))
    {}

    return (
        <div className='goods'>
            <div className="container">
                <header className='goods__header'>
                    <ArrowButton />
                    <Header />
                </header>

                <ul className="goods__main">
                    <img className='goods__main-img' src={good.url} alt="" />
                    <div className="goods__main-card">
                        <h2 className="goods__main-title">{good.title}</h2>
                        <p className='goods__main-decription'>{good.description}</p>

                        <div className='goods__footer'>
                            <div className='goods__footer-order'>
                                <span className="goods__footer-price">{good.price}&nbsp;&#8381;</span>
                                <span className="goods__footer-weight">&nbsp;/{good.weight ? `${good.weight} гр.` : `${good.quantity} шт.`}</span>
                            </div>
                            <button className="goods__footer-btn" onClick={handleClick}>В корзину</button>
                        </div>
                    </div>
                </ul>
            </div>
        </div>
    );
}

export default GoodsPage;
