import React, { useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { useDispatch, useSelector } from 'react-redux';
import { products } from '../../constants/products';
import { routes } from "../../App";
import Card from '../../components/Card';
import background from '../../assets/login.jpg';
import { setIsAuth } from "../../redux/user/userSlice";
import './ProductsPage.scss';

function ProductsPage() {
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const { isAuth } = useSelector(state => state.user)
  const { basketList, sum } = useSelector(state => state.basket)

useEffect(() => {
  if (isAuth) {
    const allCards = getAllGoods()
  }
})

  if (isAuth === false) {
    return (
      <div className="login" style={{ backgroundImage: `url(${background})` }}>
      <div className="login__form">
         <h2 className="login__form-title">Меню не доступно. Авторизуйстесь.</h2>
        <button className='login__button' onClick={() => navigate(routes.login)}>Вход</button>
      </div>
      </div>
    )
  }

  const logOut = () => {
    dispatch(setIsAuth(false))
    localStorage.removeItem('login')
    localStorage.removeItem('password')
    navigate(routes.login)
}

  return (

    <main className="menu">
      <div className="container">
        <header className="menu__header">
          <div >
            <h1 className="menu__header-title">наша продукция</h1>
          </div>
          <div className="goods__header-order">
            <p className="goods__header-qty">
              <span>{basketList.length}</span>&nbsp;товара
              на сумму <span>{sum}</span>&nbsp;&#8381;
            </p>
            <button className='goods__header-icon'
              onClick={() => navigate(routes.cart)}>
              <img src="/images/1.svg" alt="Корзина"></img>
            </button>
            <button className='goods__header-button' type="submit" onClick={logOut}>Выйти</button>
          </div>
        </header>


        <ul className="menu__cards">
          {products.map(card => {
            return (
              <li key={card.id}>
                <Card
                  card={card}
                />
              </li>
            )
          })}
        </ul>
      </div>
    </main>
  );
}

export default ProductsPage;
