import React, { useEffect, useState } from 'react';
import { routes } from '../../App';
import background from "../../assets/trade-bg.jpg";
import { Link } from 'react-router-dom';
import './GoodsPage.scss'
import { getGood } from "../../constants/products";
import { useParams } from 'react-router-dom';
import { products } from '../../constants/products';

import { useSelector } from 'react-redux';



const GoodsPage = () => {

    const { basketList, sum } = useSelector(state => state.basket)

    const { id } = useParams()
    console.log('params', id)
    const [good, setGood] = useState()

    const handleClick = () => {
        const goodWithUniqId = {...good, idx: uuidv4()}
        dispatch(addProduct(goodWithUniqId))
    }

    useEffect(() => {
        if (id) {
            const goodById = getGood(id)
            setGood(goodById)
        }
    }, [id])

    if (!good) {
        return (
            <div>Loading...</div>
        )
    }

    return (
        <div className='goods' style={{ backgroundImage: `url(${background})` }}>
            <div className="container">
                <header className='goods__header'>
                    <Link to={routes.menu}>
                        <img className='header__img' src="/images/arrow-left.svg"></img>
                    </Link>

                    <div className="header__order">
                        <p className="header__order-qty">
                            <span>{basketList.length}</span>&nbsp;товара
                            на сумму <span>{sum}</span>&nbsp;&#8381;
                        </p>
                        <Link to={routes.cart}>
                            <img className='header__icon' src="/images/1.svg"></img>
                        </Link>
                        <button className='header__button' type="submit">Выйти</button>
                    </div>
                </header>

                <ul className="goods__main">
                        <li>
                            <img className='main__image' src={good.url} alt="" />
                            <div className="main__card">
                                <h2 className="main__card-title">{good.title}</h2>
                                <p className='main__card-decription'>{good.description}</p>

                                <div className='main__card-footer'>
                                    <div className="card__footer">
                                        {/* <span className="card__footer-price">{good.price}&nbsp;&#8381;</span>
                                    <span
                                        className="card__footer-weight">&nbsp;/&nbsp;{good.weight ? `${good.weight} гр.` : `${good.quantity} шт.`}</span> */}
                                        <span className="card__footer-price">{good.price}&nbsp;&#8381;</span>
                                        <span
                                            className="card__footer-weight">&nbsp;/&nbsp;{good.weight ? `${good.weight} гр.` : `${good.quantity} шт.`}</span>
                                    </div>
                                    <button className="card__footer-btn" onClick={handleClick}>В корзину</button>

                                </div>
                            </div>

                        </li>



                </ul>
            </div>
        </div>
    );
}

export default GoodsPage;
