import React from 'react';
import { useDispatch } from 'react-redux';
import { addProduct } from '../redux/basket/basketSlice';
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from 'uuid';
import './Card.scss';

const Card = ({ card }) => {

    const { url, description, title, price, weight, quantity, id } = card



    const handleBtn = (e) => {
        e.stopPropagation()
        let item = {
          id: id,
          idx: uuidv4(),
          title: title,
          url: url,
          price: price,

        }
        console.log('item', item)
        dispatch(addProduct(item))
      }



    const handleCard = () => {
        const item = {
            id: id,
            idx: uuidv4(),
            url: url,
            price: price,
            description: description,
            weight: weight,
            quantity: quantity
        }

        navigate('/goods' + '/' + id)
    }




    return (
        <div className="card" onClick={handleCard}>
            <div className="card__top">
                <img className="card__preview" src={url} alt="" />
                <h2 className="card__title">{title}</h2>
                <p className="card__description">
                    {description}
                </p>
            </div>

            <div className="card__footer">
                <div className="card__product-price">
                    {/* {`${price}/ ${weight}`} */}
                    <span className="card__price">{price}&nbsp;&#8381;</span>
            <span className="card__weight">&nbsp;/&nbsp;{weight ? `${weight} гр.` : `${quantity} шт.`}</span>
                </div>
                <button onClick={handleBtn} className='card__price-btn'>
                 &#43; 
                </button>
            </div>

        </div>
    );
}

export default Card;
