import { Routes, Route, Navigate } from "react-router-dom"
import BasketPage from './Pages/BasketPage/BasketPage';
import ProductsPage from './Pages/ProductsPage/ProductsPage';
import LoginPage from './Pages/LoginPage/LoginPage';
import GoodsPage from './Pages/GoodsPage/GoodsPage';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setIsAuth } from './redux/user/userSlice';

export const routes = {
  menu: '/',
  cart: '/cart',
  login: '/login',
  goods: '/goods'
}

function App() {
  const dispatch = useDispatch()

  useEffect(() => {
    const login = localStorage.getItem('login')
    const password = localStorage.getItem('password')
    if (login && password) {
      dispatch(setIsAuth(true))
    }
  }, [])

  return (
    <Routes>
      <Route path={routes.menu} exact={true} element={<ProductsPage />} />
      <Route path={routes.cart} exact={true} element={<BasketPage />} />
      <Route path={routes.login} exact={true} element={<LoginPage />} />
      <Route path="/goods/:id" exact={true} element={<GoodsPage />} />
      <Route
        path="*"
        element={<Navigate to={routes.menu} replace />}
      />
    </Routes>

  );
}

export default App;
