const users = [
    {
        id: 1,
        login: 'user1',
        password: '123'
    },
    {
        id: 2,
        login: 'user2',
        password: '321'
    }
]

export const loginUser = (user) => users.find(x => x.id === users.id && x.password === users.password)

export const registerUser = (user) => users.push({
    ...user, 
})

export const getAllUsers = () => users
